INSERT INTO `wp_options` VALUES("244", "_transient_b5acd041b405b0b35ef1638cadce62f9", "O:8:\"stdClass\":21:{s:4:\"name\";s:15:\"Simply Show IDs\";s:4:\"slug\";s:15:\"simply-show-ids\";s:7:\"version\";s:5:\"1.3.3\";s:6:\"author\";s:41:\"<a href=\"http://sivel.net\">Matt Martz</a>\";s:14:\"author_profile\";s:30:\"//profiles.wordpress.org/sivel\";s:12:\"contributors\";a:1:{s:5:\"sivel\";s:30:\"//profiles.wordpress.org/sivel\";}s:8:\"requires\";s:3:\"3.0\";s:6:\"tested\";s:5:\"3.0.5\";s:13:\"compatibility\";a:23:{s:3:\"2.9\";a:1:{s:3:\"1.2\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:5:\"2.9.1\";a:1:{s:3:\"1.2\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}}s:5:\"2.9.2\";a:5:{s:3:\"1.2\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:3:\"1.3\";a:3:{i:0;i:0;i:1;i:2;i:2;i:0;}s:5:\"1.3.1\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:5:\"1.3.2\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}s:5:\"1.3.3\";a:3:{i:0;i:0;i:1;i:1;i:2;i:0;}}s:3:\"3.0\";a:2:{s:5:\"1.3.2\";a:3:{i:0;i:0;i:1;i:1;i:2;i:0;}s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}}s:5:\"3.0.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:80;i:1;i:5;i:2;i:4;}}s:5:\"3.0.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.0.3\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:0;i:1;i:1;i:2;i:0;}}s:5:\"3.0.5\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:3:\"3.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}}s:5:\"3.1.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:3:\"3.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.2.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:5:\"3.3.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:6;i:2;i:6;}}s:5:\"3.3.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:4;i:2;i:4;}}s:5:\"3.4.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:5:\"3.4.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:3;i:2;i:3;}}s:5:\"3.5.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.5.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:3:\"3.6\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.9.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:5:\"3.9.2\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:1;i:2;i:1;}}s:3:\"4.0\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}s:3:\"4.1\";a:1:{s:5:\"1.3.3\";a:3:{i:0;i:100;i:1;i:2;i:2;i:2;}}}s:6:\"rating\";d:100;s:11:\"num_ratings\";s:1:\"3\";s:7:\"ratings\";a:5:{i:5;s:1:\"3\";i:4;i:0;i:3;i:0;i:2;i:0;i:1;i:0;}s:10:\"downloaded\";i:54207;s:12:\"last_updated\";s:21:\"2010-06-26 1:40am GMT\";s:5:\"added\";s:10:\"2009-02-03\";s:8:\"homepage\";s:43:\"http://sivel.net/wordpress/simply-show-ids/\";s:8:\"sections\";a:5:{s:11:\"description\";s:756:\"<p>Simply shows the ID of Posts, Pages, Media, Links, Categories, Tags and Users in the admin tables for easy access. Very lightweight.</p>\n\n<p>A common complaint about the edit posts, pages, media, links, categories, tags and users pages is that there is no easy way to see the ID of the specific item. This plugin will add a new column titled \'ID\' that will display each items ID.</p>\n\n<p>IDs for categories, tags and users will only display when using WordPress 2.8 or newer.</p>\n\n<p>This plugin is very simplistic and thus very lightweight. To facilitate the need of a lightweight alternative to other plugins of its type this plugin was created.</p>\n\n<p>Props to <a href=\"http://flushinc.com/\">Joel Fisher</a> for getting me moving on this plugin.</p>\";s:12:\"installation\";s:290:\"<ol>\n<li>Upload the <code>simply-show-ids</code> folder to the <code>/wp-content/plugins/</code> directory or install directly through the plugin installer.</li>\n<li>Activate the plugin through the \'Plugins\' menu in WordPress or by using the link provided by the plugin installer</li>\n</ol>\";s:11:\"screenshots\";s:280:\"<ol>\n	<li>\n		<a href=\'//s.w.org/plugins/simply-show-ids/screenshot-1.png?r=1210180\' title=\'Click to view full-size screenshot 1\'><img src=\'//s.w.org/plugins/simply-show-ids/screenshot-1.png?r=1210180\' alt=\'simply-show-ids screenshot 1\' /></a>\n		<p>Edit Posts Page</p>\n	</li>\n</ol>\";s:9:\"changelog\";s:803:\"<h4>1.3.3 (2010-06-25):</h4>\n\n<ul>\n<li>More WordPress 3.0 improvements</li>\n<li>Increase minimum required WordPress version to 3.0</li>\n</ul>\n\n<h4>1.3.2 (2010-05-12):</h4>\n\n<ul>\n<li>Fix conflict with other plugins that add custom columns, props Jeremy Hou</li>\n</ul>\n\n<h4>1.3.1 (2010-04-04):</h4>\n\n<ul>\n<li>Fix incompatibilities with WordPress version prior to 3.0</li>\n</ul>\n\n<h4>1.3 (2010-04-04):</h4>\n\n<ul>\n<li>Add support to show IDs on comments and custom taxonomies</li>\n</ul>\n\n<h4>1.2 (2009-06-08):</h4>\n\n<ul>\n<li>Add support for WordPress 2.8 to show IDs for categories, tags and users.</li>\n</ul>\n\n<h4>1.1 (2009-02-05):</h4>\n\n<ul>\n<li>Increase the width of the new column to 50px from 20px to support longer IDs</li>\n</ul>\n\n<h4>1.0 (2009-02-04):</h4>\n\n<ul>\n<li>Initial Public Release</li>\n</ul>\";s:11:\"other_notes\";s:369:\"<h3>Upgrade</h3>\n<ol>\n<li>Use the plugin updater in WordPress or...</li>\n<li>Delete the previous <code>simply-show-ids</code> folder from the <code>/wp-content/plugins/</code> directory</li>\n<li>Upload the new <code>simply-show-ids</code> folder to the <code>/wp-content/plugins/</code> directory</li>\n</ol>\n<h3>Usage</h3>\n<ol>\n<li>Just install and activate.</li>\n</ol>\";}s:17:\"short_description\";s:114:\"Simply shows the ID of Posts, Pages, Media, Links, Categories, Tags and Users in the admin tables for easy access.\";s:13:\"download_link\";s:63:\"http://downloads.wordpress.org/plugin/simply-show-ids.1.3.3.zip\";s:4:\"tags\";a:12:{s:5:\"admin\";s:5:\"admin\";s:8:\"category\";s:8:\"category\";s:2:\"id\";s:2:\"id\";s:3:\"ids\";s:3:\"ids\";s:5:\"links\";s:5:\"links\";s:5:\"media\";s:5:\"media\";s:4:\"page\";s:4:\"page\";s:4:\"post\";s:4:\"Post\";s:4:\"show\";s:4:\"show\";s:6:\"simply\";s:6:\"simply\";s:3:\"tag\";s:3:\"tag\";s:4:\"user\";s:4:\"user\";}s:11:\"donate_link\";s:23:\"http://sivel.net/donate\";}", "no");
